def sort_list(list): 
    # using bubble sort 
    n = len(list) 
    for i in range(n-1, 0, -1): 
        for j in range(0, n-i-1): 
            if list[j] > list[i]: 
                list[i], list[j] = list[j], list[i] 
    return list 

print(sort_list([7, 3, 5, 10, 1])) 

