def countUniqueChars(string):
  # create a dictionary of characters
  char_dict = {}

  # iterate over the string
  for char in string:
    # check if the character is already in the dictionary
    if char in char_dict:
      # increment the count by 1
      char_dict[char] += 1
    # add the character if it is not in the dictionary
    else:
      # set the count to 1
      char_dict[char] = 1

  # return the unique character count
  return char_dict.keys()

